// Plugin.jslib  mergeInto(LibraryManager.library, { Hello: function () { window.alert(&#34;Hello, world!&#34;); }, HelloString: function (str) { window.alert(Pointer_stringify(str)); }, PrintFloatArray: function (array, size) { for(var i = 0; i &lt; size; i++) console.log(HEAPF32[(array &gt;&gt; 2) + i]); }, AddNumbers: function (x, y) { return x + y; }, StringReturnValueFunction: function () { var returnStr = &#34;bla&#34;; var bufferSize = lengthBytesUTF8(returnStr) + 1; var buffer = _malloc(bufferSize); stringToUTF8(returnStr, buffer, bufferSize); return buffer; }, BindWebGLTexture: function (texture) { GLctx.bindTexture(GLctx.TEXTURE_2D, GL.textures[texture]); }, }); using UnityEngine; using System.Runtime.InteropServices; public class NewBehaviourScript : MonoBehaviour { [DllImport(&#34;__Internal&#34;)] private static extern void Hello(); [DllImport(&#34;__Internal&#34;)] private static extern void HelloString(string str); [DllImport(&#34;__Internal&#34;)] private static extern void PrintFloatArray(float[] array, int size); [DllImport(&#34;__Internal&#34;)] private static extern int AddNumbers(int x, int y); [DllImport(&#34;__Internal&#34;)] private static extern string StringReturnValueFunction(); [DllImport(&#34;__Internal&#34;)] private static extern void BindWebGLTexture(int texture); void Start() { Hello(); HelloString(&#34;This is a string.&#34;); float[] myArray = new float[10]; PrintFloatArray(myArray, myArray.Length); int result = AddNumbers(5, 7); Debug.Log(result); Debug.Log(StringReturnValueFunction()); var texture = new Texture2D(0, 0, TextureFormat.ARGB32, false); BindWebGLTexture(texture.GetNativeTextureID()); } } if(Application.platform == RuntimePlatform.WebGLPlayer) { Hello(); }

